import React, { useState } from "react";

const Plus = () => {
  let getTime = new Date().toLocaleTimeString();
  let [time, currentTime] = useState(getTime);

  let curr = () => {
    let cur = new Date().toLocaleTimeString();
    currentTime(cur);
  };

  return (
    <div className="container">
      <h1>Hello Sir , Cuurent Time is {time}</h1>
      <p>
        this is the page in which onclicking the button current time will be
        displayed on the screen . This tim clock is customly created using the
        some Languages of front-end developing language. Hope this watch will
        help you out
      </p>
      <button onClick={curr}>Show Me</button>
    </div>
  );
};

export default Plus;
