import React, { useState } from "react";

const Plus = () => {
  const [inputData , newinputData] = useState("");
  const [newData , setnewData] = useState([]);

  const storeData = (event) => {
    newinputData(event.target.value);
  }

  const submitData = () => {
    setnewData((elem) => {
      console.log(elem);
    });
  }

  return (
    <>
      <div className="box">
        <h1>Todo List</h1>
        <input type="text" value = {inputData} placeholder="Add a Item" onChange = {storeData}/>
        <button onClick = {submitData}>+</button>
        
        {newData.map((items) => {
          return <li>items</li>
        })}

      </div>
    </>
  );
};

export default Plus;
