import React from "react";
import {FirstName} from "./Context";

const Context = () => {
  return (
    <>
      <FirstName.Consumer>{(fname) => {
          return <h2>My name is {fname}</h2>
      }}</FirstName.Consumer>
    </>
  );
};
export default Context;
