import React, { useState } from "react";

let Plus = () => {

  let [fname , newFname] = useState("");
  let [lname , newLname] = useState("");
  let [newFirst , newFirstName] = useState("");
  let [newSecond , newSecondName] = useState("");

  let inputFname = (event) => {
    newFname(event.target.value);
  }

  let inputLname = (event) => {
    newLname(event.target.value);
  }

  let submitData = (event) => {
    event.preventDefault();
    newFirstName(fname);
    newSecondName(lname);
  }





  return(
    <>
      <div className = "container">
      <form onSubmit = {submitData}>

        <h1>Hello {newFirst} {newSecond}</h1>
          <input type = "text" value = {fname} onChange = {inputFname} placeholder = "Enter Your first Name"/><br/><br/>
          <input type = "text" value = {lname} onChange = {inputLname} placeholder = "Enter Your last Name"/><br/><br/>
        <button>Click Me</button>

      </form>
      </div>
    </>
  );
}

export default Plus;
