import React, { useState } from "react";

const Plus = () => {
  const [inputData , newinputData] = useState("");

  const updateData = (event) => {
    newinputData(event.target.value);
  }

  return (
    <>
      <div className="box">
        <h1>ToDo List</h1>
        <input type="text" value = {inputData} placeholder = "Add a item" onChange = {updateData}/>
        <button>+</button>
        {inputData}
      </div>
    </>
  );
};
export default Plus;
