import React, { useState } from "react";

let Plus = () => {
    let [text , newText] = useState("hey Click!!");
    let [bg, newBg] = useState("red")
    let myFun = () => {
        newText("hello")
    }

    return (
        <div className = "container" style = {{backgroundColor : bg}}>
           <h1>{ text }</h1>
           <button onClick = {myFun}>click</button>
        </div>
    );
}

export default Plus;
