import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";


const increNumber = () => {
    const [number , setNumber] = useState(0);
    const increNum = () => {
        setNumber(number++);
    }
    return (
        <>
            <div className = "container w-25 mx-auto my-5 p-5"  >
                <h1 className = "display-1 text-center" style = {{fontFamily: 'Montserrat'}}>{number}</h1>
                <div className = "row">
                <div className = "col-10 mx-auto ">
                <button onClick = {increNum} className = "btn btn-outline-success">Increment</button>
                <button onClick = {decreNum} className = "btn btn-outline-danger">Decerement</button>
            </div>
            </div>
            </div>
        </>
    )
}

export default increNumber;
