//changing the title of the title bar using useEffect;


import React from "react";
import { useState } from "react";
let [num , newNum] = useState(0);

const Context = () => {
  return (
    <>
    <button onClick = {
      () => {
        newNum(num++);
      }
    }>Click me {num}</button>
    </>
  );
}

export default Context;
