import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import AddIcon from '@material-ui/icons/Add';
import KeepData from "./KeepData";

const KeepNote = () => {
  let [ note, newNote ] = useState({
    title : "",
    content : ""
  });

  const inputData = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    
    newNote((preValue) => {
      return {
        ...preValue ,
        [name] : value
      };
    })
  }

  return (
    <>
      <section className="container my-5 w-50 ">
        <div className="row ">
          <div className="col-md-10 col-12 mx-auto">
            <div class="mb-1">
              <input
                type="text"
                name = "title"
                placeholder="Title"
                class="form-control"
                id="exampleFormControlInput1"
                value = {note.title}
                onChange = {inputData}
              />
            </div>
            <textarea
              class="form-control"
              id="exampleFormControlTextarea1"
              rows=""
              column=""
              name = "content"
              placeholder="Save Your Note"
              value = {note.content}
              onChange = {inputData}
            ></textarea>
            <button type="button" class="btn btn-outline-info mt-1 mx-auto" onClick = {submitData}><AddIcon/></button>
          </div>
        </div>
      </section>

      <section>
        {/* <KeepData value = {dispData} value1 = {dispData1}/> */}
      </section>
    </>
  );
};

export default KeepNote;
