import React, { useState } from "react";

const Plus = () => {
  let [value, newValue] = useState(0);

  let increData = () => {
    value++;
    newValue(value);
  };
  setInterval(increData , 1000)

  let decreData = () => {
    if (value > 0) {
      value--;
      newValue(value);
    }
  };

  return (
    <>
      <div className="centre_div">
        <h1>{value}</h1>
        <button className="btn1" onClick={increData}>
          Increment
        </button>
        <button className="btn2" onClick={decreData}>
          Decrement
        </button>
      </div>
    </>
  );
};

export default Plus;
